@Composable
private fun CreateVisitorForm(
    visitorName: String,
    onNameChange: (String) -> Unit,
    nameError: String,

    email: String,
    onEmailChange: (String) -> Unit,
    emailError: String,

    phoneNumber: String,
    onPhoneChange: (String) -> Unit,
    phoneError: String,

    purposeOfVisit: String,
    onPurposeChange: (String) -> Unit,
    purposeError: String,

    selectedDate: String,
    selectedTime: String,
    onDateClick: () -> Unit,
    onTimeClick: () -> Unit,
    dateTimeError: String
) {
    Column(
        verticalArrangement = Arrangement.spacedBy(16.dp)
    ) {
        // Visitor Name
        PermitelyTextField(
            value = visitorName,
            onValueChange = onNameChange,
            label = "Visitor Name *",
            keyboardType = KeyboardType.Text,
            isError = nameError.isNotEmpty(),
            errorMessage = nameError
        )

        // Email Address
        PermitelyTextField(
            value = email,
            onValueChange = onEmailChange,
            label = "Email Address *",
            keyboardType = KeyboardType.Email,
            isError = emailError.isNotEmpty(),
            errorMessage = emailError
        )

        // Phone Number
        PermitelyTextField(
            value = phoneNumber,
            onValueChange = onPhoneChange,
            label = "Phone Number *",
            keyboardType = KeyboardType.Phone,
            isError = phoneError.isNotEmpty(),
            errorMessage = phoneError
        )

        // Purpose of Visit
        PermitelyTextField(
            value = purposeOfVisit,
            onValueChange = onPurposeChange,
            label = "Purpose of Visit *",
            keyboardType = KeyboardType.Text,
            isError = purposeError.isNotEmpty(),
            errorMessage = purposeError
        )

        // Date and Time Section
        Text(
            text = "Visit Schedule",
            style = MaterialTheme.typography.titleMedium,
            color = TextPrimary,
            fontWeight = FontWeight.Medium
        )

        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.spacedBy(12.dp)
        ) {
            // Date Picker
            DateTimeCard(
                label = "Date",
                value = selectedDate.ifEmpty { "Select Date" },
                icon = Icons.Default.DateRange,
                onClick = onDateClick,
                modifier = Modifier.weight(1f),
                isError = dateTimeError.isNotEmpty() && selectedDate.isEmpty()
            )

            // Time Picker
            DateTimeCard(
                label = "Time",
                value = selectedTime.ifEmpty { "Select Time" },
                icon = Icons.Default.Schedule,
                onClick = onTimeClick,
                modifier = Modifier.weight(1f),
                isError = dateTimeError.isNotEmpty() && selectedTime.isEmpty()
            )
        }

        // Date/Time Error
        if (dateTimeError.isNotEmpty()) {
            Text(
                text = dateTimeError,
                color = Error,
                style = MaterialTheme.typography.bodySmall,
                modifier = Modifier.padding(start = 16.dp)
            )
        }
    }
}

@Composable
private fun DateTimeCard(
    label: String,
    value: String,
    icon: androidx.compose.ui.graphics.vector.ImageVector,
    onClick: () -> Unit,
    modifier: Modifier = Modifier,
    isError: Boolean = false
) {
    Card(
        modifier = modifier,
        colors = CardDefaults.cardColors(
            containerColor = if (isError) Error.copy(alpha = 0.05f) else Surface
        ),
        elevation = CardDefaults.cardElevation(defaultElevation = 2.dp),
        shape = RoundedCornerShape(12.dp),
        onClick = onClick,
        border = if (isError) androidx.compose.foundation.BorderStroke(1.dp, Error) else null
    ) {
        Column(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            Icon(
                imageVector = icon,
                contentDescription = label,
                tint = if (isError) Error else Primary,
                modifier = Modifier.size(24.dp)
            )

            Spacer(modifier = Modifier.height(8.dp))

            Text(
                text = label,
                style = MaterialTheme.typography.labelMedium,
                color = TextSecondary
            )

            Text(
                text = value,
                style = MaterialTheme.typography.bodyMedium,
                color = if (value.contains("Select")) TextTertiary else TextPrimary,
                fontWeight = if (value.contains("Select")) FontWeight.Normal else FontWeight.Medium
            )
        }
    }
}

@Composable
private fun TimePickerDialog(
    onDismissRequest: () -> Unit,
    confirmButton: @Composable () -> Unit,
    dismissButton: @Composable () -> Unit,
    content: @Composable () -> Unit
) {
    AlertDialog(
        onDismissRequest = onDismissRequest,
        confirmButton = confirmButton,
        dismissButton = dismissButton,
        text = { content() },
        containerColor = Surface,
        titleContentColor = TextPrimary,
        textContentColor = TextPrimary
    )
}

@Composable
private fun SuccessDialog(onDismiss: () -> Unit) {
    AlertDialog(
        onDismissRequest = onDismiss,
        containerColor = Surface,
        title = {
            Row(
                verticalAlignment = Alignment.CenterVertically
            ) {
                Icon(
                    imageVector = Icons.Default.CheckCircle,
                    contentDescription = "Success",
                    tint = Success,
                    modifier = Modifier.size(24.dp)
                )
                Spacer(modifier = Modifier.width(8.dp))
                Text(
                    text = "Success!",
                    color = TextPrimary,
                    fontWeight = FontWeight.Bold
                )
            }
        },
        text = {
            Text(
                text = "Visitor appointment has been created successfully. The visitor will receive a confirmation email with visit details.",
                color = TextSecondary,
                style = MaterialTheme.typography.bodyMedium
            )
        },
        confirmButton = {
            PermitelyButton(
                text = "Done",
                onClick = onDismiss,
                modifier = Modifier.fillMaxWidth()
            )
        },
        shape = RoundedCornerShape(16.dp)
    )
}

