@Composable
private fun QuickActionsSection(
    onCreateVisitor: () -> Unit,
    onViewAllVisitors: () -> Unit,
    onViewNotifications: () -> Unit
) {
    Column {
        Text(
            text = "Quick Actions",
            style = MaterialTheme.typography.titleLarge,
            color = TextPrimary,
            fontWeight = FontWeight.Bold,
            modifier = Modifier.padding(bottom = 12.dp)
        )

        Column(
            verticalArrangement = Arrangement.spacedBy(8.dp)
        ) {
            QuickActionButton(
                title = "Create New Visitor",
                subtitle = "Register a new visitor entry",
                icon = Icons.Default.PersonAdd,
                color = Primary,
                onClick = onCreateVisitor
            )

            QuickActionButton(
                title = "View All Visitors",
                subtitle = "Browse all visitor records",
                icon = Icons.Default.List,
                color = Secondary,
                onClick = onViewAllVisitors
            )

            QuickActionButton(
                title = "View Notifications",
                subtitle = "Check recent alerts and updates",
                icon = Icons.Default.Notifications,
                color = Accent,
                onClick = onViewNotifications
            )
        }
    }
}

@Composable
private fun QuickActionButton(
    title: String,
    subtitle: String,
    icon: ImageVector,
    color: Color,
    onClick: () -> Unit
) {
    Card(
        modifier = Modifier
            .fillMaxWidth(),
        colors = CardDefaults.cardColors(
            containerColor = Surface
        ),
        elevation = CardDefaults.cardElevation(defaultElevation = 2.dp),
        shape = RoundedCornerShape(12.dp),
        onClick = onClick
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            verticalAlignment = Alignment.CenterVertically
        ) {
            Box(
                modifier = Modifier
                    .size(40.dp)
                    .background(
                        color = color.copy(alpha = 0.1f),
                        shape = CircleShape
                    ),
                contentAlignment = Alignment.Center
            ) {
                Icon(
                    imageVector = icon,
                    contentDescription = title,
                    tint = color,
                    modifier = Modifier.size(20.dp)
                )
            }

            Spacer(modifier = Modifier.width(16.dp))

            Column(
                modifier = Modifier.weight(1f)
            ) {
                Text(
                    text = title,
                    style = MaterialTheme.typography.titleMedium,
                    color = TextPrimary,
                    fontWeight = FontWeight.Medium
                )
                Text(
                    text = subtitle,
                    style = MaterialTheme.typography.bodySmall,
                    color = TextSecondary
                )
            }

            Icon(
                imageVector = Icons.Default.ChevronRight,
                contentDescription = "Navigate",
                tint = TextTertiary
            )
        }
    }
}

@Composable
private fun RecentVisitorsSection(
    viewModel: HostDashboardViewModel = hiltViewModel()
) {
    // Observe recent visitors from ViewModel
    val recentVisitors by viewModel.recentVisitors.collectAsStateWithLifecycle()
    val isLoadingVisitors by viewModel.isLoadingVisitors.collectAsStateWithLifecycle()

    Column {
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Text(
                text = "Recent Visitors",
                style = MaterialTheme.typography.titleLarge,
                color = TextPrimary,
                fontWeight = FontWeight.Bold
            )

            if (isLoadingVisitors) {
                CircularProgressIndicator(
                    modifier = Modifier.size(16.dp),
                    strokeWidth = 2.dp,
                    color = Primary
                )
            }
        }

        Spacer(modifier = Modifier.height(12.dp))

        if (isLoadingVisitors && recentVisitors.isEmpty()) {
            // Show loading state
            repeat(3) {
                RecentVisitorItemSkeleton()
                if (it < 2) Spacer(modifier = Modifier.height(8.dp))
            }
        } else if (recentVisitors.isEmpty()) {
            // Show empty state
            EmptyRecentVisitorsState()
        } else {
            // Show actual visitors from API
            recentVisitors.forEach { visitor ->
                RecentVisitorItem(visitor = visitor)
                if (visitor != recentVisitors.last()) {
                    Spacer(modifier = Modifier.height(8.dp))
                }
            }
        }
    }
}

@Composable
private fun RecentVisitorItem(visitor: RecentVisitor) {
    Card(
        modifier = Modifier.fillMaxWidth(),
        colors = CardDefaults.cardColors(
            containerColor = Surface
        ),
        elevation = CardDefaults.cardElevation(defaultElevation = 2.dp),
        shape = RoundedCornerShape(12.dp)
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            verticalAlignment = Alignment.CenterVertically
        ) {
            // Avatar
            Box(
                modifier = Modifier
                    .size(40.dp)
                    .background(
                        brush = Brush.radialGradient(
                            colors = listOf(Primary, Secondary)
                        ),
                        shape = CircleShape
                    ),
                contentAlignment = Alignment.Center
            ) {
                Text(
                    text = visitor.name.first().toString(),
                    style = MaterialTheme.typography.titleMedium,
                    color = OnPrimary,
                    fontWeight = FontWeight.Bold
                )
            }

            Spacer(modifier = Modifier.width(12.dp))

            Column(
                modifier = Modifier.weight(1f)
            ) {
                Text(
                    text = visitor.name,
                    style = MaterialTheme.typography.titleSmall,
                    color = TextPrimary,
                    fontWeight = FontWeight.Medium
                )
                Text(
                    text = visitor.purpose,
                    style = MaterialTheme.typography.bodySmall,
                    color = TextSecondary
                )
            }

            Column(
                horizontalAlignment = Alignment.End
            ) {
                Text(
                    text = visitor.time,
                    style = MaterialTheme.typography.bodySmall,
                    color = TextSecondary
                )
                Spacer(modifier = Modifier.height(2.dp))
                StatusChip(status = visitor.status)
            }
        }
    }
}

@Composable
private fun StatusChip(status: String) {
    val (backgroundColor, textColor) = when (status) {
        "Active" -> Success.copy(alpha = 0.1f) to Success
        "Completed" -> TextTertiary.copy(alpha = 0.1f) to TextTertiary
        else -> Info.copy(alpha = 0.1f) to Info
    }

    Surface(
        color = backgroundColor,
        shape = RoundedCornerShape(12.dp),
        modifier = Modifier.clip(RoundedCornerShape(12.dp))
    ) {
        Text(
            text = status,
            style = MaterialTheme.typography.labelSmall,
            color = textColor,
            modifier = Modifier.padding(horizontal = 8.dp, vertical = 4.dp),
            fontWeight = FontWeight.Medium
        )
    }
}

@Composable
private fun RecentVisitorItemSkeleton() {
    Card(
        modifier = Modifier.fillMaxWidth(),
        colors = CardDefaults.cardColors(containerColor = Surface),
        elevation = CardDefaults.cardElevation(defaultElevation = 2.dp),
        shape = RoundedCornerShape(12.dp)
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            verticalAlignment = Alignment.CenterVertically
        ) {
            // Avatar skeleton
            Box(
                modifier = Modifier
                    .size(40.dp)
                    .background(
                        color = TextTertiary.copy(alpha = 0.3f),
                        shape = CircleShape
                    )
            )

            Spacer(modifier = Modifier.width(12.dp))

            Column(modifier = Modifier.weight(1f)) {
                // Name skeleton
                Box(
                    modifier = Modifier
                        .fillMaxWidth(0.6f)
                        .height(16.dp)
                        .background(
                            color = TextTertiary.copy(alpha = 0.3f),
                            shape = RoundedCornerShape(4.dp)
                        )
                )
                Spacer(modifier = Modifier.height(4.dp))
                // Purpose skeleton
                Box(
                    modifier = Modifier
                        .fillMaxWidth(0.4f)
                        .height(14.dp)
                        .background(
                            color = TextTertiary.copy(alpha = 0.2f),
                            shape = RoundedCornerShape(4.dp)
                        )
                )
            }

            Column(horizontalAlignment = Alignment.End) {
                // Time skeleton
                Box(
                    modifier = Modifier
                        .width(40.dp)
                        .height(14.dp)
                        .background(
                            color = TextTertiary.copy(alpha = 0.2f),
                            shape = RoundedCornerShape(4.dp)
                        )
                )
                Spacer(modifier = Modifier.height(4.dp))
                // Status skeleton
                Box(
                    modifier = Modifier
                        .width(60.dp)
                        .height(20.dp)
                        .background(
                            color = TextTertiary.copy(alpha = 0.2f),
                            shape = RoundedCornerShape(12.dp)
                        )
                )
            }
        }
    }
}

