                style = MaterialTheme.typography.titleMedium,
                color = TextPrimary,
                fontWeight = FontWeight.SemiBold
            )

            if (isEditing) {
                // Editable fields
                PermitelyTextField(
                    value = name,
                    onValueChange = onNameChange,
                    label = "Full Name",
                    modifier = Modifier.fillMaxWidth()
                )

                PermitelyTextField(
                    value = email,
                    onValueChange = onEmailChange,
                    label = "Email Address",
                    keyboardType = KeyboardType.Email,
                    modifier = Modifier.fillMaxWidth()
                )

                PermitelyTextField(
                    value = phone,
                    onValueChange = onPhoneChange,
                    label = "Phone Number",
                    keyboardType = KeyboardType.Phone,
                    modifier = Modifier.fillMaxWidth()
                )
            } else {
                // Read-only display
                ProfileInfoRow(
                    icon = Icons.Default.Person,
                    label = "Full Name",
                    value = name
                )

                ProfileInfoRow(
                    icon = Icons.Default.Email,
                    label = "Email Address",
                    value = email
                )

                ProfileInfoRow(
                    icon = Icons.Default.Phone,
                    label = "Phone Number",
                    value = phone
                )

                ProfileInfoRow(
                    icon = Icons.Default.DateRange,
                    label = "Member Since",
                    value = createdAt
                )
            }
        }
    }
}

