/**
 * Notifications Screen - View in-app notifications
 * Shows notification list with read/unread status and mark all as read functionality
 */
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun NotificationsScreen(
    onNavigateBack: () -> Unit = {},
    viewModel: NotificationsViewModel = hiltViewModel()
) {
    // Observe ViewModel state
    val uiState by viewModel.uiState.collectAsStateWithLifecycle()

    // Convert API notifications to UI notifications
    val notifications = remember(uiState.notifications) {
        uiState.notifications.map { apiNotification ->
            viewModel.convertToUiNotification(apiNotification)
        }
    }

    var isMarkingAllRead by remember { mutableStateOf(false) }
    val scope = rememberCoroutineScope()

    // Count unread notifications (all are unread since API doesn't provide read status)
    val unreadCount = notifications.count { !it.isRead }

    // Handle error state
    uiState.errorMessage?.let { errorMessage ->
        LaunchedEffect(errorMessage) {
            println("Error loading notifications: $errorMessage")
            viewModel.clearError()
        }
    }

    // Mark all as read function (local UI state only)
    fun markAllAsRead() {
        scope.launch {
            isMarkingAllRead = true
            delay(1000) // Simulate API call
            // In a real implementation, you would call an API to mark as read
            isMarkingAllRead = false
        }
    }

    // Mark single notification as read (local UI state only)  
    fun markAsRead(notificationId: String) {
        // In a real implementation, you would call an API to mark as read
    }

    Box(
        modifier = Modifier
            .fillMaxSize()
            .background(Background)
    ) {
        Column(
            modifier = Modifier.fillMaxSize()
        ) {
            // Top App Bar
            NotificationsTopBar(
                onNavigateBack = onNavigateBack,
                unreadCount = unreadCount
            )

            // Content
            if (notifications.isEmpty()) {
                // Empty state
                EmptyNotificationsState()
            } else {
                LazyColumn(
                    modifier = Modifier.fillMaxSize(),
                    contentPadding = PaddingValues(16.dp),
                    verticalArrangement = Arrangement.spacedBy(12.dp)
                ) {
                    // Mark all as read button (only show if there are unread notifications)
                    if (unreadCount > 0) {
                        item {
                            MarkAllAsReadButton(
                                isLoading = isMarkingAllRead,
                                unreadCount = unreadCount,
                                onClick = { markAllAsRead() }
                            )
                        }
                    }

                    // Notifications list
                    items(notifications) { notification ->
                        NotificationCard(
                            notification = notification,
                            onMarkAsRead = { markAsRead(notification.id) }
                        )
                    }

                    // Bottom spacing
                    item {
                        Spacer(modifier = Modifier.height(16.dp))
                    }
                }
            }
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun NotificationsTopBar(
    onNavigateBack: () -> Unit,
    unreadCount: Int
) {
    Surface(
        modifier = Modifier.fillMaxWidth(),
        color = Surface,
        shadowElevation = 4.dp,
        tonalElevation = 4.dp
    ) {
        Column {
            // Add status bar spacer
            Spacer(modifier = Modifier.windowInsetsTopHeight(WindowInsets.statusBars))

            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(16.dp),
                verticalAlignment = Alignment.CenterVertically
            ) {
                IconButton(onClick = onNavigateBack) {
                    Icon(
                        imageVector = Icons.Default.ArrowBack,
                        contentDescription = "Back",
                        tint = Primary
                    )
                }

                Spacer(modifier = Modifier.width(8.dp))

                Column(modifier = Modifier.weight(1f)) {
                    Text(
                        text = "Notifications",
                        style = MaterialTheme.typography.headlineSmall,
                        color = TextPrimary,
                        fontWeight = FontWeight.Bold
                    )
                    Text(
                        text = if (unreadCount > 0) "$unreadCount unread notifications" else "All notifications read",
                        style = MaterialTheme.typography.bodyMedium,
                        color = if (unreadCount > 0) Primary else TextSecondary
                    )
                }

                // Notification bell icon with badge
                if (unreadCount > 0) {
                    Box {
                        IconButton(onClick = { /* TODO: Add notification settings */ }) {
                            Icon(
                                imageVector = Icons.Default.Notifications,
                                contentDescription = "Notifications",
                                tint = Primary
                            )
                        }
                        // Unread count badge
                        Surface(
                            modifier = Modifier
                                .size(20.dp)
                                .offset(x = 6.dp, y = 6.dp),
                            color = Error,
                            shape = CircleShape
                        ) {
                            Box(
                                contentAlignment = Alignment.Center,
                                modifier = Modifier.fillMaxSize()
                            ) {
                                Text(
                                    text = if (unreadCount > 99) "99+" else unreadCount.toString(),
                                    color = OnError,
                                    style = MaterialTheme.typography.labelSmall,
                                    fontWeight = FontWeight.Bold
                                )
                            }
                        }
                    }
                } else {
                    IconButton(onClick = { /* TODO: Add notification settings */ }) {
                        Icon(
                            imageVector = Icons.Default.NotificationsNone,
                            contentDescription = "No notifications",
                            tint = TextSecondary
                        )
                    }
                }
            }
        }
    }
}

@Composable
private fun MarkAllAsReadButton(
    isLoading: Boolean,
    unreadCount: Int,
    onClick: () -> Unit
) {
    Card(
        modifier = Modifier.fillMaxWidth(),
        colors = CardDefaults.cardColors(containerColor = Primary.copy(alpha = 0.1f)),
        shape = RoundedCornerShape(12.dp)
    ) {
        Button(
            onClick = onClick,
            modifier = Modifier
                .fillMaxWidth()
                .padding(4.dp),
            enabled = !isLoading,
            colors = ButtonDefaults.buttonColors(
                containerColor = Color.Transparent,
                contentColor = Primary
            ),
            elevation = null
        ) {
            if (isLoading) {
                CircularProgressIndicator(
                    modifier = Modifier.size(16.dp),
                    color = Primary,
                    strokeWidth = 2.dp
                )
                Spacer(modifier = Modifier.width(8.dp))
                Text("Marking as read...")
            } else {
                Icon(
                    imageVector = Icons.Default.DoneAll,
                    contentDescription = null,
                    modifier = Modifier.size(18.dp)
                )
                Spacer(modifier = Modifier.width(8.dp))
                Text("Mark all $unreadCount as read")
            }
        }
    }
}

@Composable
private fun NotificationCard(
    notification: Notification,
    onMarkAsRead: () -> Unit
) {
    Card(
        modifier = Modifier.fillMaxWidth(),
        colors = CardDefaults.cardColors(
            containerColor = if (notification.isRead) Surface else Primary.copy(alpha = 0.05f)
        ),
        elevation = CardDefaults.cardElevation(defaultElevation = if (notification.isRead) 2.dp else 4.dp),
        shape = RoundedCornerShape(12.dp),
        onClick = if (!notification.isRead) onMarkAsRead else { {} }
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            horizontalArrangement = Arrangement.spacedBy(12.dp)
        ) {
            // Notification icon
            Card(
                modifier = Modifier.size(48.dp),
                colors = CardDefaults.cardColors(
                    containerColor = getNotificationIconColor(notification.type).copy(alpha = 0.1f)
                ),
                shape = CircleShape
            ) {
                Box(
                    modifier = Modifier.fillMaxSize(),
                    contentAlignment = Alignment.Center
                ) {
                    Icon(
                        imageVector = getNotificationIcon(notification.type),
                        contentDescription = null,
                        tint = getNotificationIconColor(notification.type),
                        modifier = Modifier.size(24.dp)
                    )
                }
            }

            // Notification content
            Column(
                modifier = Modifier.weight(1f),
                verticalArrangement = Arrangement.spacedBy(4.dp)
            ) {
                // Header with visitor name and timestamp
                Row(
                    modifier = Modifier.fillMaxWidth(),
                    horizontalArrangement = Arrangement.SpaceBetween,
                    verticalAlignment = Alignment.Top
                ) {
                    Text(
                        text = notification.visitorName,
                        style = MaterialTheme.typography.titleSmall,
                        color = TextPrimary,
                        fontWeight = FontWeight.SemiBold,
                        modifier = Modifier.weight(1f),
                        maxLines = 1,
                        overflow = TextOverflow.Ellipsis
                    )

                    Text(
                        text = formatTimestamp(notification.timestamp),
                        style = MaterialTheme.typography.bodySmall,
                        color = TextSecondary
                    )
                }

                // Notification message
                Text(
                    text = notification.message,
                    style = MaterialTheme.typography.bodyMedium,
                    color = TextPrimary,
                    maxLines = 2,
                    overflow = TextOverflow.Ellipsis
                )

                // Notification type badge
                Card(
                    colors = CardDefaults.cardColors(
                        containerColor = getNotificationIconColor(notification.type).copy(alpha = 0.1f)
                    ),
                    shape = RoundedCornerShape(8.dp)
                ) {
                    Text(
                        text = notification.type.displayName,
                        modifier = Modifier.padding(horizontal = 8.dp, vertical = 4.dp),
                        style = MaterialTheme.typography.labelSmall,
                        color = getNotificationIconColor(notification.type),
                        fontWeight = FontWeight.Medium
                    )
                }
            }

            // Unread indicator
            if (!notification.isRead) {
                Card(
                    modifier = Modifier.size(8.dp),
                    colors = CardDefaults.cardColors(containerColor = Primary),
                    shape = CircleShape
                ) {}
            }
        }
    }
}

