/**
 * Visitor Details Screen - Detailed view of specific visitor
 * Shows visitor information, status timeline, QR code, and action buttons
 */
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun VisitorDetailsScreen(
    visitorId: String,
    onNavigateBack: () -> Unit = {},
    onEditVisitor: (Visitor) -> Unit = {},
    onDeleteVisitor: (String) -> Unit = {},
    onGeneratePass: (String) -> Unit = {},
    onShareQRCode: () -> Unit = {},
    viewModel: VisitorDetailsViewModel = hiltViewModel()
) {
    val scrollState = rememberScrollState()
    val scope = rememberCoroutineScope()

    // Observe ViewModel state
    val uiState by viewModel.uiState.collectAsStateWithLifecycle()

    // Load visitor details when the screen is first composed
    LaunchedEffect(visitorId) {
        viewModel.loadVisitorDetails(visitorId)
    }

    // Convert API data to UI visitor when available
    val visitor = uiState.visitorData?.let { visitorData ->
        viewModel.convertToUiVisitor(visitorData)
    }

    // Dialog states
    var showDeleteDialog by remember { mutableStateOf(false) }
    var showQRDialog by remember { mutableStateOf(false) }
    var showGeneratePassDialog by remember { mutableStateOf(false) }
    var isGeneratingPass by remember { mutableStateOf(false) }

    // Pass generation state (simulate if visitor has a pass)
    var hasPass by remember(visitor) { mutableStateOf(visitor?.status == VisitorStatus.APPROVED) }

    // Handle error state
    uiState.errorMessage?.let { errorMessage ->
        LaunchedEffect(errorMessage) {
            println("Error loading visitor details: $errorMessage")
            // You can show a snackbar or alert dialog here
            viewModel.clearError()
        }
    }

    // Show loading state
    if (uiState.isLoading) {
        Box(
            modifier = Modifier.fillMaxSize(),
            contentAlignment = Alignment.Center
        ) {
            CircularProgressIndicator(color = Primary)
        }
        return
    }

    // Show error state if visitor not found
    if (uiState.errorMessage != null && visitor == null) {
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(32.dp),
            horizontalAlignment = Alignment.CenterHorizontally,
            verticalArrangement = Arrangement.Center
        ) {
            Icon(
                imageVector = Icons.Default.Error,
                contentDescription = "Error",
                tint = Error,
                modifier = Modifier.size(64.dp)
            )
            Spacer(modifier = Modifier.height(16.dp))
            Text(
                text = "Failed to load visitor details",
                style = MaterialTheme.typography.titleMedium,
                color = TextSecondary
            )
            Text(
                text = uiState.errorMessage ?: "Unknown error",
                style = MaterialTheme.typography.bodyMedium,
                color = TextTertiary
            )
            Spacer(modifier = Modifier.height(16.dp))
            Button(
                onClick = { viewModel.refreshVisitorDetails(visitorId) },
                colors = ButtonDefaults.buttonColors(containerColor = Primary)
            ) {
                Text("Retry")
            }
        }
        return
    }

    // Show main content when visitor data is available
    visitor?.let { visitorData ->
        Box(
            modifier = Modifier
                .fillMaxSize()
                .background(Background)
        ) {
            Column(
                modifier = Modifier
                    .fillMaxSize()
                    .verticalScroll(scrollState)
            ) {
                // Top App Bar
                VisitorDetailsTopBar(
                    visitorName = visitorData.name,
                    onNavigateBack = onNavigateBack
                )

                // Content
                Column(
                    modifier = Modifier
                        .fillMaxSize()
                        .padding(16.dp),
                    verticalArrangement = Arrangement.spacedBy(16.dp)
                ) {
                    // Visitor Information Card
                    VisitorInfoCard(visitor = visitorData)

                    // Status Timeline Card
                    StatusTimelineCard(visitor = visitorData)

                    // QR Code and Pass Card (if approved)
                    if (visitorData.status == VisitorStatus.APPROVED) {
                        QRCodePassCard(
                            visitor = visitorData,
                            hasPass = hasPass,
                            onShowQR = { showQRDialog = true },
                            onGeneratePass = { showGeneratePassDialog = true }
                        )
                    }

                    // Action Buttons
                    ActionButtonsSection(
                        visitor = visitorData,
                        hasPass = hasPass,
                        onEdit = { onEditVisitor(visitorData) },
                        onDelete = { showDeleteDialog = true },
                        onGeneratePass = { showGeneratePassDialog = true },
                        onShareQR = { showQRDialog = true }
                    )
                }
            }
        }

        // Delete Confirmation Dialog
        if (showDeleteDialog) {
            DeleteVisitorDialog(
                visitorName = visitor.name,
                onConfirm = {
                    onDeleteVisitor(visitor.id)
                    showDeleteDialog = false
                    onNavigateBack()
                },
                onDismiss = { showDeleteDialog = false }
            )
        }

        // QR Code Dialog
        if (showQRDialog) {
            QRCodeDialog(
                visitor = visitor,
                onDismiss = { showQRDialog = false },
                onShare = {
                    onShareQRCode()
                    showQRDialog = false
                }
            )
        }

        // Generate Pass Dialog
        if (showGeneratePassDialog) {
            GeneratePassDialog(
                visitor = visitor,
                isLoading = isGeneratingPass,
                onConfirm = {
                    scope.launch {
                        isGeneratingPass = true
                        delay(2000) // Simulate API call
                        hasPass = true
                        isGeneratingPass = false
                        showGeneratePassDialog = false
                        onGeneratePass(visitor.id)
                    }
                },
                onDismiss = { showGeneratePassDialog = false }
            )
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun VisitorDetailsTopBar(
    visitorName: String,
    onNavigateBack: () -> Unit
) {
    TopAppBar(
        title = {
            Column {
                Text(
                    text = "Visitor Details",
                    style = MaterialTheme.typography.titleLarge,
                    color = TextPrimary,
                    fontWeight = FontWeight.SemiBold
                )
                Text(
                    text = visitorName,
                    style = MaterialTheme.typography.bodyMedium,
                    color = TextSecondary
                )
            }
        },
        navigationIcon = {
            IconButton(onClick = onNavigateBack) {
                Icon(
                    imageVector = Icons.Default.ArrowBack,
                    contentDescription = "Back",
                    tint = TextPrimary
                )
            }
        },
        colors = TopAppBarDefaults.topAppBarColors(
            containerColor = Background
        )
    )
}

@Composable
private fun VisitorInfoCard(visitor: Visitor) {
    Card(
        modifier = Modifier.fillMaxWidth(),
        colors = CardDefaults.cardColors(containerColor = Surface),
        elevation = CardDefaults.cardElevation(defaultElevation = 4.dp),
        shape = RoundedCornerShape(16.dp)
    ) {
        Column(
            modifier = Modifier.padding(20.dp),
            verticalArrangement = Arrangement.spacedBy(16.dp)
        ) {
            // Header with status
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = "Visitor Information",
                    style = MaterialTheme.typography.titleMedium,
                    color = TextPrimary,
                    fontWeight = FontWeight.SemiBold
                )

                StatusBadge(status = visitor.status)
            }

            // Profile section
            Row(
                verticalAlignment = Alignment.CenterVertically,
                horizontalArrangement = Arrangement.spacedBy(16.dp)
            ) {
                // Profile icon
                Card(
                    modifier = Modifier.size(60.dp),
                    colors = CardDefaults.cardColors(containerColor = Primary.copy(alpha = 0.1f)),
                    shape = CircleShape
                ) {
                    Box(
                        modifier = Modifier.fillMaxSize(),
                        contentAlignment = Alignment.Center
                    ) {
                        Icon(
                            imageVector = Icons.Default.Person,
                            contentDescription = null,
                            tint = Primary,
                            modifier = Modifier.size(30.dp)
                        )
                    }
                }

                // Name and basic info
                Column {
                    Text(
                        text = visitor.name,
                        style = MaterialTheme.typography.titleLarge,
                        color = TextPrimary,
                        fontWeight = FontWeight.Medium
                    )
                    Text(
                        text = "ID: ${visitor.id}",
                        style = MaterialTheme.typography.bodySmall,
                        color = TextSecondary
                    )
                }
            }

            Divider(color = Secondary.copy(alpha = 0.2f))

            // Contact details
            InfoRow(
                icon = Icons.Default.Email,
                label = "Email",
                value = visitor.email
            )

            InfoRow(
                icon = Icons.Default.Phone,
                label = "Phone",
                value = visitor.phone
            )

            InfoRow(
                icon = Icons.Default.Business,
                label = "Purpose",
                value = visitor.purpose
            )

            InfoRow(
                icon = Icons.Default.DateRange,
                label = "Visit Date",
                value = visitor.date
            )

            InfoRow(
                icon = Icons.Default.Schedule,
                label = "Visit Time",
                value = visitor.time
            )

            InfoRow(
                icon = Icons.Default.Create,
                label = "Created",
                value = visitor.createdAt
            )
        }
    }
}

@Composable
private fun StatusBadge(status: VisitorStatus) {
    val (backgroundColor, textColor) = when (status) {
        VisitorStatus.PENDING -> Secondary.copy(alpha = 0.1f) to Secondary
        VisitorStatus.APPROVED -> Success.copy(alpha = 0.1f) to Success
        VisitorStatus.REJECTED -> Error.copy(alpha = 0.1f) to Error
        VisitorStatus.EXPIRED -> Color.Gray.copy(alpha = 0.1f) to Color.Gray
    }

    Card(
        colors = CardDefaults.cardColors(containerColor = backgroundColor),
        shape = RoundedCornerShape(20.dp)
    ) {
        Text(
            text = status.displayName.uppercase(),
            modifier = Modifier.padding(horizontal = 12.dp, vertical = 6.dp),
            style = MaterialTheme.typography.labelMedium,
            color = textColor,
            fontWeight = FontWeight.Bold
        )
    }
}

@Composable
private fun StatusTimelineCard(visitor: Visitor) {
    Card(
        modifier = Modifier.fillMaxWidth(),
        colors = CardDefaults.cardColors(containerColor = Surface),
        elevation = CardDefaults.cardElevation(defaultElevation = 4.dp),
        shape = RoundedCornerShape(16.dp)
    ) {
        Column(
            modifier = Modifier.padding(20.dp),
            verticalArrangement = Arrangement.spacedBy(16.dp)
        ) {
            Text(
                text = "Status Timeline",
                style = MaterialTheme.typography.titleMedium,
                color = TextPrimary,
                fontWeight = FontWeight.SemiBold
            )

            // Timeline items (simulated)
            TimelineItem(
                icon = Icons.Default.Create,
                title = "Request Created",
                subtitle = "Visitor registration submitted",
                time = visitor.createdAt,
                isActive = true
            )

            if (visitor.status != VisitorStatus.PENDING) {
                TimelineItem(
                    icon = when (visitor.status) {
                        VisitorStatus.APPROVED -> Icons.Default.CheckCircle
                        VisitorStatus.REJECTED -> Icons.Default.Cancel
                        VisitorStatus.EXPIRED -> Icons.Default.Schedule
                        else -> Icons.Default.Info
                    },
                    title = when (visitor.status) {
                        VisitorStatus.APPROVED -> "Request Approved"
                        VisitorStatus.REJECTED -> "Request Rejected"
                        VisitorStatus.EXPIRED -> "Visit Expired"
                        else -> "Status Updated"
                    },
                    subtitle = "Status changed by host",
                    time = "Today, 10:30 AM",
                    isActive = true
                )
            }
        }
    }
}

@Composable
private fun TimelineItem(
    icon: ImageVector,
    title: String,
    subtitle: String,
    time: String,
    isActive: Boolean
) {
    Row(
        modifier = Modifier.fillMaxWidth(),
        horizontalArrangement = Arrangement.spacedBy(12.dp),
        verticalAlignment = Alignment.Top
    ) {
        Card(
            modifier = Modifier.size(40.dp),
            colors = CardDefaults.cardColors(
                containerColor = if (isActive) Primary.copy(alpha = 0.1f) else Secondary.copy(alpha = 0.1f)
            ),
            shape = CircleShape
        ) {
            Box(
                modifier = Modifier.fillMaxSize(),
                contentAlignment = Alignment.Center
            ) {
                Icon(
                    imageVector = icon,
                    contentDescription = null,
                    tint = if (isActive) Primary else Secondary,
                    modifier = Modifier.size(20.dp)
                )
            }
        }

        Column(modifier = Modifier.weight(1f)) {
            Text(
                text = title,
                style = MaterialTheme.typography.bodyMedium,
                color = TextPrimary,
                fontWeight = FontWeight.Medium
            )
            Text(
                text = subtitle,
                style = MaterialTheme.typography.bodySmall,
                color = TextSecondary
            )
            Text(
                text = time,
                style = MaterialTheme.typography.bodySmall,
                color = TextSecondary
            )
        }
    }
}

@Composable
private fun QRCodePassCard(
    visitor: Visitor,
    hasPass: Boolean,
    onShowQR: () -> Unit,
    onGeneratePass: () -> Unit
) {
    Card(
        modifier = Modifier.fillMaxWidth(),
        colors = CardDefaults.cardColors(containerColor = Success.copy(alpha = 0.05f)),
        elevation = CardDefaults.cardElevation(defaultElevation = 4.dp),
        shape = RoundedCornerShape(16.dp)
    ) {
        Column(
            modifier = Modifier.padding(20.dp),
            verticalArrangement = Arrangement.spacedBy(16.dp)
        ) {
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = "Digital Pass",
                    style = MaterialTheme.typography.titleMedium,
                    color = TextPrimary,
                    fontWeight = FontWeight.SemiBold
                )

                Icon(
                    imageVector = Icons.Default.QrCode,
                    contentDescription = null,
                    tint = Success,
                    modifier = Modifier.size(24.dp)
                )
            }

            if (visitor.hasQRCode && visitor.qrCodeUrl != null) {
                // Pass details with QR code URL
                Column(
                    verticalArrangement = Arrangement.spacedBy(12.dp)
                ) {
                    Text(
                        text = "Pass Generated Successfully",
                        style = MaterialTheme.typography.bodyMedium,
                        color = Success,
                        fontWeight = FontWeight.Medium
                    )

                    // Pass Information
                    visitor.passId?.let { passId ->
                        InfoRow(
                            icon = Icons.Default.Badge,
                            label = "Pass ID",
                            value = "PASS-$passId"
                        )
                    }

                    visitor.expiryTime?.let { expiryTime ->
                        InfoRow(
                            icon = Icons.Default.Schedule,
                            label = "Valid Until",
                            value = formatDateTime(expiryTime)
                        )
                    }

                    // QR Code URL Section
                    Card(
                        modifier = Modifier.fillMaxWidth(),
                        colors = CardDefaults.cardColors(containerColor = Primary.copy(alpha = 0.05f)),
                        shape = RoundedCornerShape(12.dp)
                    ) {
                        Column(
                            modifier = Modifier.padding(16.dp),
                            verticalArrangement = Arrangement.spacedBy(8.dp)
                        ) {
                            Row(
                                verticalAlignment = Alignment.CenterVertically,
                                horizontalArrangement = Arrangement.spacedBy(8.dp)
                            ) {
                                Icon(
                                    imageVector = Icons.Default.QrCode,
                                    contentDescription = "QR Code",
                                    tint = Primary,
                                    modifier = Modifier.size(20.dp)
                                )
                                Text(
                                    text = "QR Code Access",
                                    style = MaterialTheme.typography.titleSmall,
                                    color = TextPrimary,
                                    fontWeight = FontWeight.Medium
                                )
                            }

                            // QR Code URL Link
                            Card(
                                modifier = Modifier.fillMaxWidth(),
                                colors = CardDefaults.cardColors(containerColor = Surface),
                                shape = RoundedCornerShape(8.dp),
                                onClick = onShowQR
                            ) {
                                Column(
                                    modifier = Modifier.padding(12.dp)
                                ) {
                                    Text(
                                        text = "QR Code URL:",
                                        style = MaterialTheme.typography.labelSmall,
                                        color = TextSecondary,
                                        fontWeight = FontWeight.Medium
                                    )
                                    Spacer(modifier = Modifier.height(4.dp))
                                    Text(
                                        text = visitor.qrCodeUrl,
                                        style = MaterialTheme.typography.bodySmall,
                                        color = Primary,
                                        fontWeight = FontWeight.Medium
                                    )
                                }
                            }

                            Text(
                                text = " QR code has been emailed to visitor",
                                style = MaterialTheme.typography.bodySmall,
                                color = Success,
                                fontWeight = FontWeight.Medium
                            )
                        }
                    }

                    // Action Buttons
                    Row(
                        modifier = Modifier.fillMaxWidth(),
                        horizontalArrangement = Arrangement.spacedBy(8.dp)
                    ) {
                        Button(
                            onClick = onShowQR,
                            modifier = Modifier.weight(1f),
                            colors = ButtonDefaults.buttonColors(
                                containerColor = Primary,
                                contentColor = OnPrimary
                            )
                        ) {
                            Icon(
                                imageVector = Icons.Default.QrCode,
                                contentDescription = null,
                                modifier = Modifier.size(16.dp)
                            )
                            Spacer(modifier = Modifier.width(4.dp))
                            Text("View QR")
                        }

                        OutlinedButton(
                            onClick = { /* Share QR Code URL */ },
                            modifier = Modifier.weight(1f),
                            colors = ButtonDefaults.outlinedButtonColors(
                                contentColor = Primary
                            )
                        ) {
                            Icon(
                                imageVector = Icons.Default.Share,
                                contentDescription = null,
                                modifier = Modifier.size(16.dp)
                            )
                            Spacer(modifier = Modifier.width(4.dp))
                            Text("Share")
                        }
                    }
                }
            } else if (hasPass) {
                // Legacy pass without QR code URL (fallback)
                Column(
                    verticalArrangement = Arrangement.spacedBy(8.dp)
                ) {
                    Text(
                        text = "Pass Generated Successfully",
                        style = MaterialTheme.typography.bodyMedium,
                        color = Success,
                        fontWeight = FontWeight.Medium
                    )

                    Text(
                        text = "Valid until: ${visitor.date} ${visitor.time}",
                        style = MaterialTheme.typography.bodySmall,
                        color = TextSecondary
                    )

                    Text(
                        text = "Pass ID: PASS-${visitor.id.uppercase()}",
                        style = MaterialTheme.typography.bodySmall,
                        color = TextSecondary
                    )
                }

                // QR Code preview placeholder
                Card(
                    modifier = Modifier
                        .size(120.dp)
                        .align(Alignment.CenterHorizontally),
                    colors = CardDefaults.cardColors(containerColor = Surface),
                    onClick = onShowQR
                ) {
                    Box(
                        modifier = Modifier.fillMaxSize(),
                        contentAlignment = Alignment.Center
                    ) {
                        Column(
                            horizontalAlignment = Alignment.CenterHorizontally,
                            verticalArrangement = Arrangement.Center
                        ) {
                            Icon(
                                imageVector = Icons.Default.QrCode,
                                contentDescription = "QR Code",
                                tint = Primary,
                                modifier = Modifier.size(40.dp)
                            )
                            Text(
                                text = "Tap to view",
                                style = MaterialTheme.typography.bodySmall,
                                color = TextSecondary,
                                textAlign = TextAlign.Center
                            )
                        }
                    }
                }
            } else {
                Text(
                    text = "No pass generated yet. Generate a digital pass for easy entry.",
                    style = MaterialTheme.typography.bodyMedium,
                    color = TextSecondary
                )

                Button(
