        }

        // Light brand particles (kept minimal to stay corporate)
        SubtleParticles(visible = phase >= SplashPhase.ContentIn)
    }
}

private enum class SplashPhase { Initial, BrandIn, ContentIn, Loading, Exit }

// ---------------------- Background ---------------------- //
@Composable
private fun CorporateGridBackground(visible: Boolean) {
    val inf = rememberInfiniteTransition(label = "grid")
    val shift by inf.animateFloat(
        initialValue = 0f,
        targetValue = 1f,
        animationSpec = infiniteRepeatable(tween(6000, easing = LinearEasing)),
        label = "grid_shift"
    )

    AnimatedVisibility(visible = visible, enter = fadeIn(tween(500))) {
        Canvas(
            Modifier
                .fillMaxSize()
                .alpha(0.12f)
        ) {
            val step = 28.dp.toPx()
            val offset = step * shift
            val w = size.width
            val h = size.height
            val gridColor = Primary.copy(alpha = 0.35f)

            // Diagonal grid (\\ lines)
            var x = -h
            while (x < w + h) {
                drawLine(
                    color = gridColor,
                    start = Offset(x + offset, 0f),
                    end = Offset(x - h + offset, h),
                    strokeWidth = 1.dp.toPx()
                )
                x += step
            }
            // Soft vignette
            drawRect(
                brush = Brush.radialGradient(
                    listOf(Color.Transparent, Color.Black.copy(alpha = 0.25f)),
                    center = center,
                    radius = min(size.width, size.height)
                )
            )
        }
    }
}

@Composable
private fun SubtleParticles(visible: Boolean) {
    val inf = rememberInfiniteTransition(label = "particles")
    val t by inf.animateFloat(
        initialValue = 0f,
        targetValue = 1f,
        animationSpec = infiniteRepeatable(tween(16000, easing = LinearEasing)),
        label = "particles_t"
    )
    val dots = remember {
        List(8) {
            Particle(
                base = Offset(Random.nextFloat(), Random.nextFloat()),
                r = Random.nextFloat() * 18f + 8f,
                speed = Random.nextFloat() * 0.35f + 0.15f,
                alpha = Random.nextFloat() * 0.15f + 0.06f
            )
        }
    }

    AnimatedVisibility(visible = visible, enter = fadeIn(tween(400))) {
        Canvas(Modifier.fillMaxSize()) {
            dots.forEach { p ->
                val x = (p.base.x + t * p.speed).mod(1f)
                val y = (p.base.y + sin(t * 2 * PI * p.speed).toFloat() * 0.07f).mod(1f)
                drawCircle(
                    color = Primary.copy(alpha = p.alpha),
                    radius = p.r,
                    center = Offset(x * size.width, y * size.height)
                )
            }
        }
    }
}

private data class Particle(
    val base: Offset,
    val r: Float,
    val speed: Float,
    val alpha: Float
)

// ---------------------- Brand Mark ---------------------- //
@Composable
private fun BrandMark(modifier: Modifier = Modifier, phase: SplashPhase) {
    val inf = rememberInfiniteTransition(label = "brand")

    // Pulse for subtle life
    val pulse by inf.animateFloat(
        initialValue = 0.96f,
        targetValue = 1.04f,
        animationSpec = infiniteRepeatable(tween(1800, easing = FastOutSlowInEasing), RepeatMode.Reverse),
        label = "brand_pulse"
    )

    // Radar sweep rotation
    val sweep by inf.animateFloat(
        0f, 360f,
        animationSpec = infiniteRepeatable(tween(4200, easing = LinearEasing)),
        label = "radar_sweep"
    )

    val visible by animateFloatAsState(
        targetValue = if (phase >= SplashPhase.BrandIn) 1f else 0f,
        animationSpec = spring(dampingRatio = Spring.DampingRatioLowBouncy, stiffness = Spring.StiffnessLow),
        label = "brand_visible"
    )

    Box(
        modifier = modifier
            .size(220.dp)
            .scale(pulse * visible)
            .alpha(visible),
        contentAlignment = Alignment.Center
    ) {
        // Segmented security ring build-in
        SegmentedRing(progress = visible)

        // Core mark: shield + stylized P + check
        Surface(
            modifier = Modifier.size(120.dp),
            shape = CircleShape,
            color = Primary,
            tonalElevation = 8.dp,
            shadowElevation = 18.dp
        ) {
            Box(Modifier.fillMaxSize(), contentAlignment = Alignment.Center) {
                Canvas(Modifier.fillMaxSize()) {
                    drawPermitlyMark(this)

                    // Radar sweep sector (security scan vibe)
                    rotate(sweep) {
                        val pad = size.minDimension * 0.08f
                        drawArc(
                            brush = Brush.radialGradient(
                                listOf(Color.White.copy(alpha = 0.22f), Color.Transparent)
                            ),
                            startAngle = -10f,
                            sweepAngle = 40f,
                            useCenter = true,
                            topLeft = Offset(pad, pad),
                            size = Size(size.width - 2 * pad, size.height - 2 * pad)
                        )
                    }
                }
            }
        }

        // Outer soft glows (concentric, very subtle)
        repeat(2) { i ->
            Box(
                modifier = Modifier
                    .size((160 + i * 26).dp)
                    .clip(CircleShape)
                    .background(Primary.copy(alpha = 0.06f - (i * 0.02f)))
                    .blur((3 + i).dp)
            )
        }
    }
}

private fun drawPermitlyMark(scope: DrawScope) = with(scope) {
    val w = size.width
    val h = size.height
    val cx = w / 2
    val cy = h / 2

    // Shield base
    val shieldPath = Path().apply {
        val top = Offset(cx, h * 0.18f)
        val left = Offset(w * 0.22f, h * 0.36f)
        val right = Offset(w * 0.78f, h * 0.36f)
        val bottom = Offset(cx, h * 0.82f)
        moveTo(top.x, top.y)
        cubicTo(w * 0.30f, h * 0.28f, w * 0.26f, h * 0.46f, cx, h * 0.60f)
        cubicTo(w * 0.74f, h * 0.46f, w * 0.70f, h * 0.28f, right.x, right.y)
        lineTo(left.x, left.y)
        close()
    }
    drawPath(shieldPath, color = OnPrimary.copy(alpha = 0.08f))

    // Stylized "P" path (visitor Pass / Permit)
    val pStroke = Stroke(width = w * 0.08f, cap = StrokeCap.Round, join = StrokeJoin.Round)
    val pPath = Path().apply {
        val start = Offset(w * 0.30f, h * 0.42f)
        moveTo(start.x, start.y)
        // Vertical stem
        lineTo(w * 0.30f, h * 0.72f)
        // Bowl
        moveTo(w * 0.30f, h * 0.42f)
        quadraticBezierTo(w * 0.64f, h * 0.34f, w * 0.60f, h * 0.52f)
        quadraticBezierTo(w * 0.58f, h * 0.62f, w * 0.42f, h * 0.60f)
    }
    drawPath(pPath, color = OnPrimary.copy(alpha = 0.90f), style = pStroke)

    // Secure checkmark overlay (admission granted)
    val check = Path().apply {
        moveTo(w * 0.38f, h * 0.58f)
        lineTo(w * 0.46f, h * 0.66f)
        lineTo(w * 0.64f, h * 0.46f)
    }
    drawPath(check, color = Secondary, style = Stroke(width = w * 0.065f, cap = StrokeCap.Round))
}

@Composable
private fun SegmentedRing(progress: Float) {
    // Shows as four secure gates lighting on
    Canvas(Modifier.size(190.dp)) {
        val stroke = 5.dp.toPx()
        val inset = 10.dp.toPx()
        val rect = Rect(Offset(inset, inset), Size(size.width - inset * 2, size.height - inset * 2))
        val segments = 4
        val gap = 18f
        val sweepPer = (360f - segments * gap) / segments
        for (i in 0 until segments) {
            val start = i * (sweepPer + gap) - 90f
            val segProgress = (progress * segments) - i
            val shown = segProgress.coerceIn(0f, 1f)
            if (shown > 0f) {
                drawArc(
                    brush = Brush.sweepGradient(listOf(Primary, Secondary, Primary)),
                    startAngle = start,
                    sweepAngle = sweepPer * shown,
                    useCenter = false,
                    style = Stroke(width = stroke, cap = StrokeCap.Round),
                    topLeft = rect.topLeft,
                    size = rect.size
                )
            }
        }
    }
}

@Composable
private fun UnderlineAccent() {
    val inf = rememberInfiniteTransition(label = "underline")
    val width by inf.animateFloat(0.6f, 1f, infiniteRepeatable(tween(1600), RepeatMode.Reverse), label = "underline_w")
    Box(
        modifier = Modifier
            .height(4.dp)
            .width((110 * width).dp)
            .clip(RoundedCornerShape(2.dp))
            .background(
                brush = Brush.horizontalGradient(
                    listOf(Color.Transparent, Primary, Secondary, Primary, Color.Transparent)
                )
            )
    )
}

// ---------------------- Visitor Pass Loader ---------------------- //
@Composable
private fun VisitorPassLoader() {
    val inf = rememberInfiniteTransition(label = "pass")
    val scanX by inf.animateFloat(0f, 1f, infiniteRepeatable(tween(1600, easing = LinearEasing)), label = "scan_x")

    Surface(
        modifier = Modifier
            .width(220.dp)
            .height(120.dp),
        shape = RoundedCornerShape(16.dp),
        tonalElevation = 6.dp,
        shadowElevation = 10.dp,
        color = Background.copy(alpha = 0.85f)
    ) {
        Box(Modifier.fillMaxSize()) {
            // Top strip (like an ID lanyard accent)
            Box(
                Modifier
                    .fillMaxWidth()
                    .height(10.dp)
                    .background(Primary)
            )

            // Barcode placeholder
            Canvas(
                Modifier
                    .align(Alignment.BottomCenter)
                    .padding(bottom = 14.dp)
                    .width(160.dp)
                    .height(28.dp)
            ) {
                val barW = 4.dp.toPx()
                val space = 2.dp.toPx()
                var x = 0f
                while (x < size.width) {
                    drawRect(color = TextPrimary.copy(alpha = 0.5f), topLeft = Offset(x, 0f), size = Size(barW, size.height))
                    x += barW + space
                }
            }

            // Scanning line
            Canvas(
                Modifier
                    .fillMaxSize()
                    .padding(horizontal = 16.dp, vertical = 10.dp)
            ) {
                val y = size.height * 0.55f
                val left = 0f
                val right = size.width
                drawLine(
                    brush = Brush.horizontalGradient(
                        listOf(Color.Transparent, Accent.copy(alpha = 0.8f), Color.Transparent)
                    ),
                    start = Offset(left + (right - left) * (scanX * 0.9f), y),
                    end = Offset(left + (right - left) * (scanX * 0.9f + 0.15f), y),
                    strokeWidth = 4.dp.toPx(),
                    cap = StrokeCap.Round
                )
            }

            // Labels
            Column(
                Modifier
                    .fillMaxSize()
                    .padding(top = 20.dp, start = 16.dp, end = 16.dp)
            ) {
                Text(
                    text = "Visitor Pass",
                    color = TextPrimary,
                    fontWeight = FontWeight.SemiBold,
                    fontSize = 14.sp
                )
                Spacer(Modifier.height(6.dp))
                Text(
                    text = "Validating access",
                    color = TextSecondary,
                    fontSize = 12.sp
                )
            }
        }
    }
}
